"use client";

import { motion } from "framer-motion";
import { Button } from "../ui/Button";
import Link from "next/link";
import { useEffect, useState } from "react";

const typingWords = [
    "AI Full-Stack Development",
    "DairyTech Innovation",
    "AgriTech Platforms",
    "PropTech Solutions"
];

export function HeroSection() {
    const [currentWord, setCurrentWord] = useState(0);

    useEffect(() => {
        const interval = setInterval(() => {
            setCurrentWord((prev) => (prev + 1) % typingWords.length);
        }, 3000);
        return () => clearInterval(interval);
    }, []);

    return (
        <section className="relative min-h-screen flex items-center justify-center pt-24 pb-20 overflow-hidden bg-slate-950">
            {/* Background elements */}
            <div className="absolute inset-0 z-0">
                <div className="absolute top-1/4 left-1/4 w-[500px] h-[500px] bg-blue-600/20 rounded-full blur-[120px] mix-blend-screen pointer-events-none"></div>
                <div className="absolute bottom-1/4 right-1/4 w-[600px] h-[600px] bg-teal-600/10 rounded-full blur-[150px] mix-blend-screen pointer-events-none"></div>
                <div className="absolute inset-0 bg-[linear-gradient(to_right,#80808012_1px,transparent_1px),linear-gradient(to_bottom,#80808012_1px,transparent_1px)] bg-[size:24px_24px]"></div>
            </div>

            <div className="container mx-auto px-4 max-w-7xl relative z-10 text-center">
                <motion.div
                    initial={{ opacity: 0, y: 30 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ duration: 0.8, ease: "easeOut" }}
                >
                    <h1 className="text-5xl md:text-7xl lg:text-8xl font-bebas-neue tracking-wide text-white mb-6 uppercase leading-tight">
                        Empowering Businesses with <br />
                        <span className="text-gradient">Scalable Software Solutions</span>
                    </h1>

                    <div className="h-10 md:h-12 flex justify-center items-center mb-10">
                        <motion.p
                            key={currentWord}
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            className="text-xl md:text-3xl font-khand font-bold text-slate-300"
                        >
                            {typingWords[currentWord]}
                        </motion.p>
                    </div>

                    <motion.p
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        transition={{ delay: 0.4 }}
                        className="text-slate-400 text-lg md:text-xl max-w-2xl mx-auto mb-12 font-light text-balance"
                    >
                        We build the intelligent, enterprise-grade foundations for the world's most critical supply chains and platforms.
                    </motion.p>

                    <motion.div
                        initial={{ opacity: 0, y: 20 }}
                        animate={{ opacity: 1, y: 0 }}
                        transition={{ delay: 0.6 }}
                        className="flex flex-col sm:flex-row items-center justify-center gap-4"
                    >
                        <Link href="/contact">
                            <Button variant="gradient" size="lg" className="w-full sm:w-auto h-14 px-8 text-lg font-bold tracking-wide uppercase">
                                Talk to Us
                            </Button>
                        </Link>
                        <Link href="/services">
                            <Button variant="outline" size="lg" className="w-full sm:w-auto h-14 px-8 text-lg font-bold tracking-wide uppercase">
                                Explore Solutions
                            </Button>
                        </Link>
                    </motion.div>
                </motion.div>
            </div>
        </section>
    );
}

"use client";

import { useState, useEffect } from "react";
import Link from "next/link";
import { Menu, X, ChevronDown } from "lucide-react";
import { Button } from "./Button";
import { cn } from "@/lib/utils";
import { motion, AnimatePresence } from "framer-motion";

const navLinks = [
    { name: "Home", href: "/" },
    { name: "About Us", href: "/about" },
    { name: "Services", href: "/services" },
];

const industryLinks = [
    { name: "DairyTech", href: "/industries/dairytech" },
    { name: "AgriTech", href: "/industries/agritech" },
    { name: "PropTech", href: "/industries/proptech" },
];

const secondaryLinks = [
    { name: "Projects", href: "/projects" },
    { name: "Impact", href: "/impact" },
    { name: "Insights", href: "/insights" },
    { name: "Careers", href: "/careers" },
    { name: "Contact", href: "/contact" },
];

export function Navbar() {
    const [scrolled, setScrolled] = useState(false);
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
    const [dropdownOpen, setDropdownOpen] = useState(false);
    const [mobileDropdownOpen, setMobileDropdownOpen] = useState(false);

    useEffect(() => {
        const handleScroll = () => {
            setScrolled(window.scrollY > 20);
        };
        window.addEventListener("scroll", handleScroll);
        return () => window.removeEventListener("scroll", handleScroll);
    }, []);

    return (
        <header
            className={cn(
                "fixed top-0 w-full z-50 transition-all duration-300",
                scrolled ? "glass border-b border-white/10 py-3" : "bg-transparent py-5"
            )}
        >
            <div className="container mx-auto px-4 md:px-8 max-w-[1400px]">
                <div className="flex items-center justify-between">

                    {/* Logo Section */}
                    <div className="flex-shrink-0 mr-8">
                        <Link href="/" className="flex items-center gap-2 z-50">
                            <span className="font-bebas-neue text-3xl tracking-widest text-white">
                                RAJADI<span className="text-blue-500 ml-1">GLOBAL</span>
                            </span>
                        </Link>
                    </div>

                    {/* Desktop Nav */}
                    <nav className="hidden lg:flex items-center justify-center space-x-1 flex-1">
                        {navLinks.map((link) => (
                            <Link
                                key={link.name}
                                href={link.href}
                                className="px-4 py-2 text-sm font-khand font-bold tracking-wide text-slate-300 hover:text-white transition-colors uppercase relative group"
                            >
                                {link.name}
                                <span className="absolute -bottom-1 left-4 right-4 h-0.5 bg-blue-500 scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></span>
                            </Link>
                        ))}

                        {/* Dropdown Menu */}
                        <div
                            className="relative px-4 py-2"
                            onMouseEnter={() => setDropdownOpen(true)}
                            onMouseLeave={() => setDropdownOpen(false)}
                        >
                            <button className="flex items-center gap-1 text-sm font-khand font-bold tracking-wide text-slate-300 hover:text-white transition-colors uppercase cursor-pointer group">
                                Industries
                                <ChevronDown size={16} className={cn("transition-transform duration-300", dropdownOpen && "rotate-180")} />
                                <span className="absolute -bottom-1 left-4 right-4 h-0.5 bg-blue-500 scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></span>
                            </button>

                            <AnimatePresence>
                                {dropdownOpen && (
                                    <motion.div
                                        initial={{ opacity: 0, y: 10, scale: 0.95 }}
                                        animate={{ opacity: 1, y: 0, scale: 1 }}
                                        exit={{ opacity: 0, y: 10, scale: 0.95 }}
                                        transition={{ duration: 0.2 }}
                                        className="absolute top-full left-0 mt-2 w-56 rounded-xl glass border border-white/10 shadow-2xl overflow-hidden py-2"
                                    >
                                        {industryLinks.map((item) => (
                                            <Link
                                                key={item.name}
                                                href={item.href}
                                                className="block px-6 py-3 text-sm font-khand font-bold tracking-wide text-slate-300 hover:text-white hover:bg-slate-800/50 transition-colors uppercase"
                                                onClick={() => setDropdownOpen(false)}
                                            >
                                                {item.name}
                                            </Link>
                                        ))}
                                        <div className="mx-4 my-2 h-px bg-white/10"></div>
                                        <Link
                                            href="/industries"
                                            className="block px-6 py-3 text-xs font-khand font-bold tracking-wider text-blue-400 hover:text-blue-300 transition-colors uppercase"
                                            onClick={() => setDropdownOpen(false)}
                                        >
                                            View All Industries
                                        </Link>
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>

                        {secondaryLinks.map((link) => (
                            <Link
                                key={link.name}
                                href={link.href}
                                className="px-4 py-2 text-sm font-khand font-bold tracking-wide text-slate-300 hover:text-white transition-colors uppercase relative group"
                            >
                                {link.name}
                                <span className="absolute -bottom-1 left-4 right-4 h-0.5 bg-blue-500 scale-x-0 group-hover:scale-x-100 transition-transform origin-left"></span>
                            </Link>
                        ))}
                    </nav>

                    {/* CTA & Mobile Toggle */}
                    <div className="flex items-center gap-4">
                        <div className="hidden lg:block">
                            <Link href="/contact">
                                <Button variant="gradient" className="rounded-full px-6 font-bold shadow-blue-500/20 shadow-lg border border-blue-400/20 uppercase tracking-wide text-xs">
                                    Get Consultation
                                </Button>
                            </Link>
                        </div>

                        {/* Mobile Menu Toggle */}
                        <button
                            className="lg:hidden relative z-50 p-2 text-slate-300 hover:text-white"
                            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                        >
                            <motion.div animate={mobileMenuOpen ? "open" : "closed"} className="flex flex-col gap-1.5 pointer-events-none">
                                <motion.span variants={{ closed: { rotate: 0, y: 0 }, open: { rotate: 45, y: 6 } }} className="w-6 h-0.5 bg-current block transition-all"></motion.span>
                                <motion.span variants={{ closed: { opacity: 1 }, open: { opacity: 0 } }} className="w-6 h-0.5 bg-current block transition-all"></motion.span>
                                <motion.span variants={{ closed: { rotate: 0, y: 0 }, open: { rotate: -45, y: -6 } }} className="w-6 h-0.5 bg-current block transition-all"></motion.span>
                            </motion.div>
                        </button>
                    </div>
                </div>
            </div>

            {/* Mobile Nav Overlay */}
            <AnimatePresence>
                {mobileMenuOpen && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        exit={{ opacity: 0 }}
                        className="fixed inset-0 z-40 bg-slate-950/95 backdrop-blur-xl lg:hidden flex flex-col pt-24 pb-8 px-6 overflow-y-auto"
                    >
                        <div className="flex flex-col gap-6 font-khand text-2xl font-bold uppercase tracking-wide">
                            {navLinks.map((link, i) => (
                                <motion.div key={link.name} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: i * 0.05 }}>
                                    <Link href={link.href} className="text-slate-300 hover:text-white block" onClick={() => setMobileMenuOpen(false)}>
                                        {link.name}
                                    </Link>
                                </motion.div>
                            ))}

                            {/* Mobile Dropdown */}
                            <motion.div initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: navLinks.length * 0.05 }} className="border-y border-white/5 py-4 my-2">
                                <button
                                    className="flex items-center justify-between w-full text-slate-300 hover:text-white text-left"
                                    onClick={() => setMobileDropdownOpen(!mobileDropdownOpen)}
                                >
                                    Industries
                                    <ChevronDown className={cn("transition-transform duration-300", mobileDropdownOpen && "rotate-180")} />
                                </button>
                                <AnimatePresence>
                                    {mobileDropdownOpen && (
                                        <motion.div
                                            initial={{ height: 0, opacity: 0 }}
                                            animate={{ height: "auto", opacity: 1 }}
                                            exit={{ height: 0, opacity: 0 }}
                                            className="overflow-hidden flex flex-col gap-4 mt-4 pl-4 border-l-2 border-slate-800"
                                        >
                                            {industryLinks.map((item) => (
                                                <Link
                                                    key={item.name}
                                                    href={item.href}
                                                    className="text-xl text-slate-400 hover:text-blue-400 block"
                                                    onClick={() => setMobileMenuOpen(false)}
                                                >
                                                    {item.name}
                                                </Link>
                                            ))}
                                            <Link href="/industries" className="text-sm tracking-wider text-blue-500 mt-2" onClick={() => setMobileMenuOpen(false)}>
                                                VIEW ALL
                                            </Link>
                                        </motion.div>
                                    )}
                                </AnimatePresence>
                            </motion.div>

                            {secondaryLinks.map((link, i) => (
                                <motion.div key={link.name} initial={{ opacity: 0, x: -20 }} animate={{ opacity: 1, x: 0 }} transition={{ delay: (navLinks.length + 1 + i) * 0.05 }}>
                                    <Link href={link.href} className="text-slate-300 hover:text-white block" onClick={() => setMobileMenuOpen(false)}>
                                        {link.name}
                                    </Link>
                                </motion.div>
                            ))}
                        </div>

                        <motion.div
                            initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ delay: 0.6 }}
                            className="mt-auto pt-8 border-t border-white/10"
                        >
                            <Link href="/contact" onClick={() => setMobileMenuOpen(false)}>
                                <Button variant="gradient" className="w-full text-lg uppercase font-bold tracking-wider py-6">
                                    Get Consultation
                                </Button>
                            </Link>
                        </motion.div>
                    </motion.div>
                )}
            </AnimatePresence>
        </header>
    );
}
